import re
from typing import List

from langflow.custom import Component
from langflow.inputs import MessageTextInput, IntInput
from langflow.io import Output, MultilineInput
from langflow.schema.data import Data


class KnowledgeRetrieverComponent(Component):
    """Search through uploaded documents and knowledge sources."""

    display_name = "Knowledge Search"
    description = "Search through uploaded documents and knowledge sources"
    icon = "BookOpen"

    inputs = [
        MessageTextInput(
            name="query",
            display_name="Search Query",
            info="What to search for in your knowledge base",
            tool_mode=True,
            required=True,
        ),
        MultilineInput(
            name="knowledge_content",
            display_name="Knowledge Content",
            info="The content from your uploaded documents (injected automatically)",
            required=True,
            advanced=True,
        ),
        IntInput(
            name="top_k",
            display_name="Number of Results",
            info="How many relevant passages to return",
            value=3,
            advanced=True,
            required=False,
        ),
    ]

    outputs = [
        Output(display_name="Search Results", name="results", type_=Data, method="search"),
    ]

    def _simple_tokenize(self, text: str) -> List[str]:
        """Simple word tokenization."""
        return re.findall(r'\b\w+\b', text.lower())

    def _calculate_relevance(self, query_tokens: List[str], chunk: str) -> float:
        """Calculate simple relevance score based on token overlap."""
        chunk_tokens = set(self._simple_tokenize(chunk))
        if not chunk_tokens:
            return 0.0
        matches = sum(1 for token in query_tokens if token in chunk_tokens)
        return matches / len(query_tokens) if query_tokens else 0.0

    def search(self) -> Data:
        """Search the knowledge base and return relevant passages."""
        try:
            query = str(self.query).strip()
            content = str(self.knowledge_content).strip()
            top_k = self.top_k or 3

            if not content:
                return Data(text="No knowledge content available.", data={"results": []})

            if not query:
                return Data(text="Please provide a search query.", data={"results": []})

            # Split content into chunks (by paragraphs or fixed size)
            # First try splitting by double newlines (paragraphs)
            chunks = [c.strip() for c in re.split(r'\n\n+', content) if c.strip()]

            # If chunks are too large, split further
            processed_chunks = []
            for chunk in chunks:
                if len(chunk) > 1000:
                    # Split large chunks by sentences
                    sentences = re.split(r'(?<=[.!?])\s+', chunk)
                    current_chunk = ""
                    for sentence in sentences:
                        if len(current_chunk) + len(sentence) > 800:
                            if current_chunk:
                                processed_chunks.append(current_chunk.strip())
                            current_chunk = sentence
                        else:
                            current_chunk += " " + sentence
                    if current_chunk:
                        processed_chunks.append(current_chunk.strip())
                else:
                    processed_chunks.append(chunk)

            if not processed_chunks:
                return Data(text="Could not process knowledge content.", data={"results": []})

            # Score each chunk
            query_tokens = self._simple_tokenize(query)
            scored_chunks = []
            for i, chunk in enumerate(processed_chunks):
                score = self._calculate_relevance(query_tokens, chunk)
                if score > 0:
                    scored_chunks.append((score, i, chunk))

            # Sort by score and get top_k
            scored_chunks.sort(reverse=True, key=lambda x: x[0])
            top_chunks = scored_chunks[:top_k]

            if not top_chunks:
                return Data(
                    text=f"No relevant information found for: {query}",
                    data={"results": [], "query": query}
                )

            # Format results
            results = []
            result_text_parts = []
            for i, (score, idx, chunk) in enumerate(top_chunks, 1):
                results.append({
                    "passage": chunk,
                    "relevance_score": round(score, 3),
                    "chunk_index": idx
                })
                result_text_parts.append(f"[Passage {i}]\n{chunk}")

            result_text = "\n\n---\n\n".join(result_text_parts)
            self.status = f"Found {len(results)} relevant passages"

            return Data(
                text=result_text,
                data={"results": results, "query": query}
            )

        except Exception as e:
            error_msg = f"Search error: {str(e)}"
            self.status = error_msg
            return Data(text=error_msg, data={"error": str(e)})
